from pydantic import BaseModel, EmailStr, Field
from datetime import datetime

class UserIn(BaseModel):
    username: str
    name: str
    email: EmailStr
    password: str

    class Config:
        schema_extra = {
            "example": {
                "username": "john_doe",
                "name": "John Doe",
                "email": "johndoe@example.com",
                "password": "strongpassword123",
            }
        }


class UserLoginSchema(BaseModel):
    email: EmailStr
    password: str

    class Config:
        schema_extra = {
            "example": {
                "email": "johndoe@example.com",
                "password": "strongpassword123",
            }
        }


class UserOut(BaseModel):
    id: str
    username: str
    name: str
    email: EmailStr
    created_at: datetime

    class Config:
        orm_mode = True
        schema_extra = {
            "example": {
                "id": "60e7ab63b23c7e1c5cd6d2b5",
                "username": "john_doe",
                "name": "John Doe",
                "email": "johndoe@example.com",
                "created_at": "2021-01-01T00:00:00Z",
            }
        }


class UserOutWithToken(UserOut):
    token: str

    class Config:
        schema_extra = {
            "example": {
                "id": "60e7ab63b23c7e1c5cd6d2b5",
                "username": "john_doe",
                "name": "John Doe",
                "email": "johndoe@example.com",
                "created_at": "2021-01-01T00:00:00Z",
                "token": "your-jwt-token-here",
            }
        }
